import React, { useState, useEffect, useRef } from "react";
import { useLocation } from "react-router-dom";
import "./css/slider.css";

export default function Slider() {
  const location = useLocation();
  const { images, title } = location.state || { images: [], title: "No Title" };

  const [slideIndex, setSlideIndex] = useState(0);
  const zoomContainerRef = useRef(null);
  const zoomResultRef = useRef(null);

  // Function to go to next or previous slide
  const plusSlides = (n) => {
    let newIndex = slideIndex + n;
    if (newIndex >= images.length) newIndex = 0;
    if (newIndex < 0) newIndex = images.length - 1;
    setSlideIndex(newIndex);
  };

  // Function to select a specific slide
  const currentSlide = (n) => {
    setSlideIndex(n);
  };

  // Zoom functionality
  useEffect(() => {
    function imageZoom(imgID, resultID) {
      const img = document.getElementById(imgID);
      const result = document.getElementById(resultID);
      let lens, cx, cy;

      // Remove any existing lens
      const existingLens = img.parentElement.querySelector(".img-zoom-lens");
      if (existingLens) existingLens.remove();

      // Create lens
      lens = document.createElement("DIV");
      lens.setAttribute("class", "img-zoom-lens");
      img.parentElement.insertBefore(lens, img);

      // Calculate the ratio between result DIV and lens
      cx = result.offsetWidth / lens.offsetWidth;
      cy = result.offsetHeight / lens.offsetHeight;

      // Set background properties for the result DIV
      result.style.backgroundImage = `url('${img.src}')`;
      result.style.backgroundSize = `${img.width * cx}px ${img.height * cy}px`;

      // Add mousemove and touchmove listeners
      lens.addEventListener("mousemove", moveLens);
      img.addEventListener("mousemove", moveLens);
      lens.addEventListener("touchmove", moveLens);
      img.addEventListener("touchmove", moveLens);

      function moveLens(e) {
        e.preventDefault();
        const pos = getCursorPos(e, img);
        let x = pos.x - lens.offsetWidth / 2;
        let y = pos.y - lens.offsetHeight / 2;

        if (x > img.width - lens.offsetWidth) x = img.width - lens.offsetWidth;
        if (x < 0) x = 0;
        if (y > img.height - lens.offsetHeight) y = img.height - lens.offsetHeight;
        if (y < 0) y = 0;

        lens.style.left = `${x}px`;
        lens.style.top = `${y}px`;
        result.style.backgroundPosition = `-${x * cx}px -${y * cy}px`;
      }

      function getCursorPos(e, img) {
        const a = img.getBoundingClientRect();
        const x = e.pageX - a.left - window.pageXOffset;
        const y = e.pageY - a.top - window.pageYOffset;
        return { x, y };
      }
    }

    if (images[slideIndex]) {
      imageZoom(`img-${slideIndex}`, "img-zoom-result");
    }
  }, [slideIndex, images]);

  return (
    <div className="slider-page">
      <h2>{title}</h2>
      <div className="slider-container">
      <a className="prev" onClick={() => plusSlides(-1)}>
          &#10094;
        </a>

        {images.map((img, index) => (
          <div
            className={`slider-image ${index === slideIndex ? "show" : ""}`}
            key={index}
          >
            <img
              id={`img-${index}`}
              src={img}
              alt={`Slide ${index + 1}`}
              style={{ width: "100%" }}
            />
          </div>
        ))}

        <a className="next" onClick={() => plusSlides(1)}>
          &#10095;
        </a>

        <div className="row">
          {images.map((img, index) => (
            <div className="column" key={index}>
              <img
                className={`demo cursor ${index === slideIndex ? "active" : ""}`}
                src={img}
                alt={`Thumbnail ${index + 1}`}
                style={{ width: "100%" }}
                onClick={() => currentSlide(index)}
              />
            </div>
          ))}
        </div>
      </div>

      <div className="img-zoom-container" ref={zoomContainerRef}>
        <div className="img-holder">
          <img id={`img-${slideIndex}`} src={images[slideIndex]} alt="Zoomable" />
        </div>
        <div className="img-zoom-result" id="img-zoom-result" ref={zoomResultRef}></div>
      </div>
    </div>
  );
}
