import React, { useState, useEffect, useRef } from "react";
import { useLocation } from "react-router-dom";
import "./css/slider.css";

export default function Slider() {
  const location = useLocation();
  const { images, title } = location.state || { images: [], title: "No Title" };

  const [slideIndex, setSlideIndex] = useState(0);
  const zoomContainerRef = useRef(null);
  const zoomResultRef = useRef(null);
  const zoomImgRef = useRef(null);
  const imgHolderRef = useRef(null);

  // Function to go to next or previous slide
  const plusSlides = (n) => {
    let newIndex = slideIndex + n;
    if (newIndex >= images.length) newIndex = 0;
    if (newIndex < 0) newIndex = images.length - 1;
    setSlideIndex(newIndex);
  };

  // Function to select a specific slide
  const currentSlide = (n) => {
    setSlideIndex(n);
  };

  // Zoom functionality
  useEffect(() => {
    const img = zoomImgRef.current;
    const result = zoomResultRef.current;
    const imgHolder = imgHolderRef.current;
    let lens, cx, cy;

    if (!img || !result || !imgHolder) return;

    // Remove any existing lens
    if (imgHolder.querySelector(".img-zoom-lens")) {
      imgHolder.querySelector(".img-zoom-lens").remove();
    }

    // Create lens
    lens = document.createElement("DIV");
    lens.setAttribute("class", "img-zoom-lens");
    imgHolder.appendChild(lens);

    // Calculate the ratio between result DIV and lens
    cx = result.offsetWidth / lens.offsetWidth;
    cy = result.offsetHeight / lens.offsetHeight;

    // Set background properties for the result DIV
    result.style.backgroundImage = `url('${img.src}')`;
    result.style.backgroundSize = `${img.width * cx}px ${img.height * cy}px`;

    // Add mousemove listeners to imgHolder
    const moveLens = (e) => {
      e.preventDefault();
      const pos = getCursorPos(e, imgHolder);
      let x = pos.x - lens.offsetWidth / 2;
      let y = pos.y - lens.offsetHeight / 2;

      // Confine lens within imgHolder boundaries
      if (x > imgHolder.offsetWidth - lens.offsetWidth) x = imgHolder.offsetWidth - lens.offsetWidth;
      if (x < 0) x = 0;
      if (y > imgHolder.offsetHeight - lens.offsetHeight) y = imgHolder.offsetHeight - lens.offsetHeight;
      if (y < 0) y = 0;

      lens.style.left = `${x}px`;
      lens.style.top = `${y}px`;
      result.style.backgroundPosition = `-${x * cx}px -${y * cy}px`;
    };

    const getCursorPos = (e, container) => {
      const a = container.getBoundingClientRect();
      const x = e.pageX - a.left - window.pageXOffset;
      const y = e.pageY - a.top - window.pageYOffset;
      return { x, y };
    };

    imgHolder.addEventListener("mousemove", moveLens);
    imgHolder.addEventListener("touchmove", moveLens);

    return () => {
      imgHolder.removeEventListener("mousemove", moveLens);
      imgHolder.removeEventListener("touchmove", moveLens);
      if (lens) lens.remove();
    };
  }, [slideIndex]);

  return (
    <div className="slider-page">
      <h2>{title}</h2>
      <div className="slider-container">
        <div className="slider-container-container">
          <a className="prev" onClick={() => plusSlides(-1)}>
            &#10094;
          </a>

          {images.map((img, index) => (
            <div
              className={`slider-image ${index === slideIndex ? "show" : ""}`}
              key={index}
            >
              <img src={img} alt={`Slide ${index + 1}`} style={{ width: "100%" }} />
            </div>
          ))}

          <a className="next" onClick={() => plusSlides(1)}>
            &#10095;
          </a>
        </div>

        <div className="row">
          {images.map((img, index) => (
            <div className="column" key={index}>
              <img
                className={`demo cursor ${index === slideIndex ? "active" : ""}`}
                src={img}
                alt={`Thumbnail ${index + 1}`}
                style={{ width: "100%" }}
                onClick={() => currentSlide(index)}
              />
            </div>
          ))}
        </div>
      </div>

      <div className="img-zoom-container" ref={zoomContainerRef}>
        <div className="img-holder" ref={imgHolderRef}>
          <img
            ref={zoomImgRef}
            src={images[slideIndex]}
            alt="Zoomable"
            style={{ width: "100%" }}
          />
        </div>
        <div className="img-zoom-result" ref={zoomResultRef}></div>
      </div>
    </div>
  );
}
